package com.sb.problems.strings;

public class LPS2 {

    static void printSubStr(
            String str, int low, int high)
    {
        System.out.println(
                str.substring(
                        low, high + 1));
    }

    // This function prints the longest
    // palindrome substring of str[].
    // It also returns the length of the
    // longest palindrome
    static String longestPalSubstr(String str)
    {
        // get length of input string
        int n = str.length();

        // table[i][j] will be false if
        // substring str[i..j] is not palindrome.
        // Else table[i][j] will be true
        boolean table[][] = new boolean[n][n];

        // All substrings of length 1 are palindromes
        int maxLength = 1;
        int start = 0;
        for (int i = 0; i < n; ++i){
            table[i][i] = true;
            if (i+1 < n && str.charAt(i) == str.charAt(i + 1)) {
                table[i][i + 1] = true;
                start = i;
                maxLength = 2;
            }
        }
        // Check for lengths greater than 2.
        // i is length of substring
        for (int i = 3; i <= n; ++i) {

            // Fix the starting index
            for (int j = 0; j < n - i + 1; ++j) {
                // Get the ending index of substring from
                // starting index j and length i
                int k = j + i - 1;

                // checking for sub-string from ith index to
                // jth index iff str.charAt(j+1) to
                // str.charAt(k-1) is a palindrome
                if (table[j + 1][k - 1]
                        && str.charAt(j) == str.charAt(k)) {
                    table[j][k] = true;

                    if (i > maxLength) {
                        start = j;
                        maxLength = i;
                    }
                }
            }
        }
        printSubStr(str, start,
                start + maxLength - 1);

        return str.substring(
                start, start + maxLength);
        // return length of LPS
    }

    public static void main(String[] args) {
        LPS2 obj = new LPS2();
        obj.longestPalSubstr("abb");
        obj.longestPalSubstr("aaaabaaa");
        //System.out.println(obj.longestPalSubstr("abb"));
        //System.out.println(obj.longestPalSubstr(""));

    }
}
