package com.sb.problems.strings;

public class LongestPalindromeSubstring {

    public static void main(String[] args) {
        LongestPalindromeSubstring obj = new LongestPalindromeSubstring();
        /*System.out.println(obj.getLargestPalindromeSubstring("aaaabaaa"));
        System.out.println(obj.getLargestPalindromeSubstring("forgeeksskeegfor"));
        System.out.println(obj.getLargestPalindromeSubstring("Geeks"));
        System.out.println(obj.getLargestPalindromeSubstring("ABCDCBA"));
        System.out.println(obj.getLargestPalindromeSubstring(""));*/
        System.out.println(obj.getLargestPalindromeSubstring(""));
    }

    private String getLargestPalindromeSubstring(String A) {
        if(A.length() == 0) {
            return A;
        }
        LongestStringInfo info = new LongestStringInfo(A.substring(0,1));
        Boolean[][] matrix = new Boolean[A.length()][A.length()];

        for(int i = 0; i < A.length(); i++) {
            matrix[i][i] = true;
            if(i+1 < A.length() && A.charAt(i) == A.charAt(i + 1)) {
                matrix[i][i + 1] = true;
                if(info.longestSubstring.length() < 2) {
                    info.longestSubstring = A.substring(i, i+2);
                }
            }
        }

        for(int i = 0; i < A.length(); i++) {
            for(int j = i+2; j < A.length(); j++) {
                if(matrix[i][j] != null) {
                    continue;
                }
                matrix[i][j] = isPalindrome(A, i, j, matrix, info);
            }
        }
        return info.longestSubstring;
    }

    Boolean isPalindrome(String A, int i, int j, Boolean matrix[][], LongestStringInfo info) {
        if(A.charAt(i) == A.charAt(j)) {
            if(matrix[i+1][j-1] == null) {
                matrix[i+1][j-1] = isPalindrome(A, i+1, j-1, matrix, info);
            }
            if(matrix[i+1][j-1]) {
                if(info.longestSubstring.length() < (j+1) - i) {
                    info.longestSubstring = A.substring(i, j+1);
                }
                return true;
            }
        }
        return false;
    }

    private static class LongestStringInfo {
        String longestSubstring;
        LongestStringInfo(String longestSubstring) {
            this.longestSubstring = longestSubstring;
        }
    }
}

